INSERT INTO products (id, product_name, product_price, product_category, src, product_description) VALUES (1, 'Lindt Dark Choc', 5.4, 'Snack', 'assets/1.jpg', 'The Lindt Master Chocolatiers have created this masterpiece using whole hazelnuts, roasted to perfection, mixed with crunchy caramelised hazelnut pieces surrounded by smooth dark chocolate.');
INSERT INTO products (id, product_name, product_price, product_category, src, product_description) VALUES (2, 'Bakery Fairy Cupcakes', 4.5, 'Bakery', 'assets/2.jpg', 'May Contain TREE NUTS, May Contain PEANUTS, May Contain SULPHITES, Contains CEREALS CONTAINING GLUTEN, Contains MILK, Contains EGG, Contains SOY, May Contain SESAME');
INSERT INTO products (id, product_name, product_price, product_category, src, product_description) VALUES (3, 'Schweppes Soda Water', 2.5, 'Drink', 'assets/3.jpg', 'Schweppes Soda Water is available in 4x300mL glass packs, 1.1L PET and 2L PET.');
