INSERT INTO products (id, product_name, product_price, product_category, src, product_description) VALUES (5, 'Banana Bread Slices 5pack', 4.7, 'Bakery', 'assets/5.jpg', 'Less sugar but same great banana taste, the perfect healthier snack on the go.');
INSERT INTO products (id, product_name, product_price, product_category, src, product_description) VALUES (6, 'Frozen Pink Dragonfruit Chunks', 2.8, 'Frozen', 'assets/6.jpg', 'Take up your breakfast a notch higher with Creative Gourmet Frozen Pink Dragonfruit Chunks. Make your own dragonfruit smoothies or mix your granola with some dragonfruit chunks for a delicious, fruity taste.');
INSERT INTO products (id, product_name, product_price, product_category, src, product_description) VALUES (7, 'Greek Style Natural Yoghurt', 5.5, 'Dairy', 'assets/7.jpg', 'Farmers Union Greek style yogurt is perfect with breakfast, in cooking or as a snack. This versatile, healthy and all natural Greek style yogurt will Make Anything Zing! With the goodness of calcium, no artificial colours, flavours or preservatives.');